mapper.update:

update authorities
set authority = #{authority,jdbcType=VARCHAR},
  enable = #{enable,jdbcType=BIT},
  remark = #{remark,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, authority, enable, remark
from authorities
where id = #{id,jdbcType=INTEGER}

select id, authority, enable, remark
from authorities
where enable=#{enable, jdbcType=BOOLEAN}

select id, authority, enable, remark
from authorities
where authority=#{authority, jdbcType=VARCHAR}
