mapper.select:
select count(1) as total
from classroom_schedule


		enable=#{enable,jdbcType=BOOLEAN}


		and classroom_id=#{classroomId,jdbcType=INTEGER}

select id, classroom_id, date, enable, c1, c2, c3, c4, c5, c6, c7, c8, c9, c10, c11, 
c12
from classroom_schedule


		enable=#{enable,jdbcType=BOOLEAN}


		and classroom_id=#{classroomId,jdbcType=INTEGER}


	limit #{index,jdbcType=INTEGER},#{rows,jdbcType=INTEGER}
