from personal_files


		enable=#{enable,jdbcType=BOOLEAN}


		and users_username=#{usersUsername,jdbcType=VARCHAR}


		and position like concat(#{positionPrefixLike,jdbcType=VARCHAR},'%')




	enable=#{enable,jdbcType=BOOLEAN}


	and users_username=#{usersUsername,jdbcType=VARCHAR}


	and position like concat(#{positionPrefixLike,jdbcType=VARCHAR},'%')


limit #{index,jdbcType=INTEGER},#{rows,jdbcType=INTEGER}
