mapper.update:

update roles
set role = #{role,jdbcType=VARCHAR},
  enable = #{enable,jdbcType=BIT},
  remark = #{remark,jdbcType=VARCHAR}
where id = #{id,jdbcType=INTEGER}

mapper.select:

select id, role, enable, remark
from roles
where id = #{id,jdbcType=INTEGER}

select id, role, enable, remark
from roles
where role = #{role,jdbcType=VARCHAR}

select roles.id, roles.role, roles.enable, roles.remark, authorities.id as authorities_id,
	authorities.authority as authorities_authority, authorities.enable as authorities_enable,
	authorities.remark as authorities_remark
from roles left join roles_authorities on  roles.id=roles_authorities.roles_id 
left join authorities on roles_authorities.authorities_id=authorities.id and authorities.enable=#{authoritiesEnable,jdbcType=BIT}
where roles.enable=#{rolesEnable,jdbcType=BIT}

mapper.update:
update roles
set enable = #{enable,jdbcType=BIT}
where id = #{id,jdbcType=INTEGER}

mapper.select:
select id, role, enable, remark
