mapper.insert:

insert into students (users_username, name, gender, 
  campus, department, major, 
  grade, classnum, dormitory
  )
values (#{usersUsername,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{gender,jdbcType=VARCHAR}, 
  #{campus,jdbcType=VARCHAR}, #{department,jdbcType=VARCHAR}, #{major,jdbcType=VARCHAR}, 
  #{grade,jdbcType=INTEGER}, #{classnum,jdbcType=INTEGER}, #{dormitory,jdbcType=VARCHAR}
  )

mapper.select:
select users_username, name, gender, campus, department, major, grade, classnum, 
dormitory
from students,users 

	students.users_username=users.username 

		and users.enable=#{enable,jdbcType=BOOLEAN}


limit #{index,jdbcType=INTEGER},#{rows,jdbcType=INTEGER}
