mapper.update:

update workers
set name = #{name,jdbcType=VARCHAR},
  gender = #{gender,jdbcType=VARCHAR},
  position = #{position,jdbcType=VARCHAR}
where users_username = #{usersUsername,jdbcType=VARCHAR}

mapper.select:

select users_username, name, gender, position
from workers
where users_username = #{usersUsername,jdbcType=VARCHAR}

  select users_username, name, gender, position
  from workers,users 

	workers.users_username=users.username 

		and users.enable=#{enable,jdbcType=BOOLEAN}


limit #{index,jdbcType=INTEGER},#{rows,jdbcType=INTEGER}
